import React, { Component } from "react";
import axios from "axios";
import "antd/dist/antd.css";
import { Link } from 'react-router-dom';
import { Menu } from "antd";
import { Calendar } from 'antd';
const { SubMenu } = Menu;

function onPanelChange(value, mode) {
    console.log(value.format('YYYY-MM-DD'), mode);
  }

class MenuHorizontal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      api: "http://localhost:1337",
    };
  }
  

  render() {
    return<div style={{marginLeft: '20px'}} className="row">
            <Menu mode="horizontal"style={{ width: 1500}}>
                <SubMenu  title="Trang Chủ">Trang chủ</SubMenu>
                
                <SubMenu  title="Nhân Viên">
                    <Menu.Item > <Link to='/add'>Phòng 1</Link> </Menu.Item>
                    <Menu.Item >Phòng 2</Menu.Item>
                </SubMenu>
                <SubMenu  title="Chấm Công">
                    <Menu.Item >Phòng 1</Menu.Item>
                    <Menu.Item >Phòng 2</Menu.Item>
                </SubMenu>
            </Menu>
            
            <div style={{width: 1000, marginBottom:50, marginLeft:150, marginTop:50}}>
                
                <Calendar onPanelChange={onPanelChange} />
            </div>
            
    </div>
    
  }
}

export default MenuHorizontal;
