import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import MenuHorizontal from "../Menu/MenuHorizontal";
import { Pagination } from 'antd';
import { Drawer, Form, Button, Col, Row, Input, Select, DatePicker } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
function showTotal(total) {
  return `Total ${total} items`;
}

class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      api: "http://localhost:1337",
    };
    this.componentDidMount = this.componentDidMount.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }
  async deleteItem(id) {
    let deletePst = await axios.delete(`${this.state.api}/employee/${id}`);

    if (deletePst.statusText === 'OK') {
      this.getData()
    }
  }
  state = { visible: false };
  showDrawer = () => {
    this.setState({
      visible: true,
    });
  };

  onClose = () => {
    this.setState({
      visible: false,
    });
  };
  componentDidMount() {
    this.getData();
  }

  getData = async () => {
    let data = await axios.get(`${this.state.api}/employee`);

    if (data.statusText === 'OK') {
      this.setState({
        data: data.data
      });
    } else {
      this.setState({
        data: []
      });
    }
  }


  render() {
    return<div><MenuHorizontal/>
    <div style={{marginLeft: '150px'}} className="row">
      <div className="col-xs-10 col-sm-10 col-md-10 col-lg-10">
        {/* <Link style={{margin: '15px 10px 20px 20px '}} className="btn btn-primary" href="" to='/add'>Thêm Mới</Link> */}
      <div style={{marginTop: '100px'}}>
        <table class="table">
            <thead class="thead-dark">
              <tr>
              <th style={{ width: '10%' }} className="text-center">id</th>
                <th style={{ width: '20%' }} className="text-center">Name</th>
                <th style={{ width: '15%' }} className="text-center">Gender</th>
                <th style={{ width: '20%' }} className="text-center">Date Of Birth</th>
                <th style={{ width: '15%' }} className="text-center">Phone Number</th>
                <th style={{ width: '20%' }} className="text-center">Hành Động</th>
              </tr>
            </thead>
            <tbody>
              {this.state.data.map((el, index) => {
                return (
                  <tr key={index}>
                    <td className="text-center">{el.id}</td>
                    <td className="text-center">{el.name}</td>
                    <td className="text-center">{el.gender}</td>
                    <td className="text-center">{el.dateofbirth}</td>
                    <td className="text-center">{el.phonenumber}</td>
                    <td>
                    <Button type="primary" onClick={this.showDrawer}>
                             <PlusOutlined /> New account
                    </Button>
                    <Drawer
          title="Create a new account"
          width={720}
          onClose={this.onClose}
          visible={this.state.visible}
          bodyStyle={{ paddingBottom: 80 }}
          footer={
            <div
              style={{
                textAlign: 'right',
              }}
            >
              <Button onClick={this.onClose} style={{ marginRight: 8 }}>
                Cancel
              </Button>
              <Button onClick={this.onClose} type="primary">
                Submit
              </Button>
            </div>
          }
        ></Drawer>
                      <Link className="btn btn-primary" style={{ margin: ' 0 40px 0 30px' }} to={`/editemployee/${el.id}`}>Edit</Link>
                      <button type="button" className="btn btn-danger" onClick={() => this.deleteItem(el.id)}>Xóa</button>
                    </td>
                  </tr>
                )
              })}
            </tbody>
          </table>
        <Pagination size="small" total={50} showTotal={showTotal} style={{float:"right"}}/>
      </div>
      </div> 
      </div>
    </div>;
  }
}

export default List;
