import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
class Add extends Component {
    constructor(props) {
        super(props);
        this.state = {
            autoID:'',
            date: '',
            name: '',
            start_time:'',
            gender: '',
            dateofbirth: '',
            phonenumber:'',
            api: "http://localhost:1337",
        }
        this.changeInput = this.changeInput.bind(this);
        this.addData = this.addData.bind(this);
    }
    changeInput = (event) => {
        const value = event.target.value;
        this.setState(
            { [event.target.name]: value }
        );
    }
    addData = async (e) => {
        const { autoID, date, name, start_time, gender, dateofbirth, phonenumber } = this.state;
        await axios.post(`${this.state.api}/timekeeping`, { autoID:autoID, date:date, name: name, start_time: start_time, gender:gender, dateofbirth : dateofbirth, phonenumber: phonenumber });
        
    }
    render() {
        return (
            <div  style={{margin: '30px 20px 20px 20px'}} className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                <form>
                    <div className="form-group">
                        <label style={{marginTop: '20px'}}>autoID</label>
                        <input type='text' name="autoID" onChange={this.changeInput} value={this.state.autoID} className="form-control" />
                        <label style={{marginTop: '20px'}}>Date</label>
                        <input type='text' name="date" onChange={this.changeInput} value={this.state.date} className="form-control" />
                        <label style={{marginTop: '20px'}}>Name</label>
                        <input type='text' name="name" onChange={this.changeInput} value={this.state.name} className="form-control" />
                        <label style={{marginTop: '20px'}}>Start Time</label>
                        <input type='text' name="start_time" onChange={this.changeInput} value={this.state.start_time} className="form-control" />
                        <label style={{marginTop: '20px'}}>Gender</label>
                        <input type='text' name="gender" onChange={this.changeInput} value={this.state.gender} className="form-control" />
                        <label style={{marginTop: '20px'}}>Date Of Birth</label>
                        <input type='text' name="dateofbirth" onChange={this.changeInput} value={this.state.dateofbirth} className="form-control" />
                        <label style={{marginTop: '20px'}}>Phone Number</label>
                        <input type='text' name="phonenumber" onChange={this.changeInput} value={this.state.phonenumber} className="form-control" />
                        <div style={{ margin: '20px 0 0 20px' }}>
                            <button style={{ marginRight: '30px' }} className="btn btn-primary" onClick={() => this.addData()}>Thêm Mới</button>
                            <Link className="btn btn-success" to='/'>Quay về</Link>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Add;
