import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import MenuHorizontal from "../Menu/MenuHorizontal";
import { Pagination } from 'antd';

function showTotal(total) {
  return `Total ${total} items`;
}

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      api: "http://localhost:1337",
    };
    this.componentDidMount = this.componentDidMount.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }
  async deleteItem(id) {
    let deletePst = await axios.delete(`${this.state.api}/timekeeping/${id}`);

    if (deletePst.statusText === 'OK') {
      this.getData()
    }
  }

  componentDidMount() {
    this.getData();
  }

  getData = async () => {
    let data = await axios.get(`${this.state.api}/timekeeping`);

    if (data.statusText === 'OK') {
      this.setState({
        data: data.data
      });
    } else {
      this.setState({
        data: []
      });
    }
  }


  render() {
    return<div><MenuHorizontal/>
    <div style={{marginLeft: '150px'}} className="row">
      <div className="col-xs-10 col-sm-10 col-md-10 col-lg-10">
        {/* <Link style={{margin: '15px 10px 20px 20px '}} className="btn btn-primary" href="" to='/add'>Thêm Mới</Link> */}
      <div style={{marginTop: '100px'}}>
      <table class="table">
            <thead class="thead-dark">
            <tr>
              <th style={{ width: '5%' }} className="text-center">AutoID</th>
              <th style={{ width: '5%' }} className="text-center">ID</th>
              <th style={{ width: '10%' }} className="text-center">Date</th>
              <th style={{ width: '15%' }} className="text-center">Name</th>
              <th style={{ width: '10%' }} className="text-center">Start Time</th>
              <th style={{ width: '10%' }} className="text-center">Gender</th>
              {/* <th style={{ width: '10%' }} className="text-center">Date Of Birth</th> */}
              <th style={{ width: '15%' }} className="text-center">Phone Number</th>
              <th style={{ width: '20%' }} className="text-center">Hành Động</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map((el, index) => {
              return (
                <tr key={index}>
                  <td className="text-center">{el.id}</td>
                  <td className="text-center">{el.id_name}</td>
                  <td className="text-center">{el.date}</td>
                  <td className="text-center">{el.name}</td>
                  <td className="text-center">{el.start_time}</td>
                  <td className="text-center">{el.gender}</td>
                  {/* <td className="text-center">{el.dateofbirth}</td> */}
                  <td className="text-center">{el.phonenumber}</td>
                  <td>
                    <Link className="btn btn-primary" style={{ margin: ' 0 40px 0 30px' }} to={`/edit/${el.id}`}>Edit</Link>
                    <button type="button" className="btn btn-danger" onClick={() => this.deleteItem(el.id)}>Xóa</button>
                  </td>
                </tr>
                
              )
            })}
          </tbody>
        </table>
        <Pagination size="small" total={50} showTotal={showTotal} style={{float:"right"}}/>
      </div>
      </div> 
      </div>
    </div>;
  }
}

export default Home;
