import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
class Edit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            api: 'http://localhost:1337',
            data: []
        }
        this.componentDidMount = this.componentDidMount.bind(this);
        this.changeInput = this.changeInput.bind(this);
        this.updateData = this.updateData.bind(this);
    }
    async componentDidMount() {
        const id = this.props.match.params.id;
        const data = await axios.get(`${this.state.api}/employee/${id}`);
        this.setState({ data: data.data });


    }
    changeInput = (event) => {
        const value = event.target.value;

        this.setState({
            data: {
                ...this.state.data,
                [event.target.name]: value
            }
        });
    }

    updateData = async (e) => {
        e.preventDefault()
        const {id, name, gender, dateofbirth, phonenumber } = this.state.data;
        let update = await axios.put(`${this.state.api}/employee/${id}`, { id:id, name: name, gender:gender, dateofbirth : dateofbirth, phonenumber: phonenumber})
        if (update.status === 200) alert("Cập nhật thành công")
        else alert("Cập nhật thất bại")
    }
    render() {

        return (
            <div style={{margin: '30px 20px 20px 20px'}} className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                <form>
                    <div className="form-group">
                        <label style={{marginTop: '20px'}}>id</label>
                        <input type='text' name="autoID" onChange={this.changeInput} value={this.state.autoID} className="form-control" />
                        
                        <label style={{marginTop: '20px'}}>Name</label>
                        <input type='text' name="name" onChange={this.changeInput} value={this.state.name} className="form-control" />
                        
                        <label style={{marginTop: '20px'}}>Gender</label>
                        <input type='text' name="gender" onChange={this.changeInput} value={this.state.gender} className="form-control" />
                        <label style={{marginTop: '20px'}}>Date Of Birth</label>
                        <input type='text' name="dateofbirth" onChange={this.changeInput} value={this.state.dateofbirth} className="form-control" />
                        <label style={{marginTop: '20px'}}>Phone Number</label>
                        <input type='text' name="phonenumber" onChange={this.changeInput} value={this.state.phonenumber} className="form-control" />
                        <div style={{ margin: '20px 0 0 20px' }}>
                            <button style={{ marginRight: '30px' }} className="btn btn-primary" onClick={(e) => this.updateData(e)}>Cập Nhật</button>
                            <Link className="btn btn-success" to='/'>Quay về</Link>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Edit;
