import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import { Menu } from "antd";
import {
  AppstoreOutlined,
  MailOutlined,
  SettingOutlined
} from "@ant-design/icons";
const { SubMenu } = Menu;
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      api: "http://localhost:1337",
    };
    this.componentDidMount = this.componentDidMount.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }
  async deleteItem(id) {
    let deletePst = await axios.delete(`${this.state.api}/timekeeping/${id}`);

    if (deletePst.statusText === 'OK') {
      this.getData()
    }
  }

  componentDidMount() {
    this.getData();
  }

  getData = async () => {
    let data = await axios.get(`${this.state.api}/timekeeping`);

    if (data.statusText === 'OK') {
      this.setState({
        data: data.data
      });
    } else {
      this.setState({
        data: []
      });
    }
  }

  render() {
    return <div style={{marginLeft: '20px'}} className="row">
      <div className="col-xs-10 col-sm-10 col-md-10 col-lg-10">
        {/* <Link style={{margin: '15px 10px 20px 20px '}} className="btn btn-primary" href="" to='/add'>Thêm Mới</Link> */}
        <Menu
        mode="horizontal"
        openKeys={this.state.openKeys}
        onOpenChange={this.onOpenChange}
        style={{ width: 256 }}
      >
        <SubMenu
          key="sub1"
          title={
            <span>
              <MailOutlined />
              <span>Navigation One</span>
            </span>
          }
        >
          <Menu.Item key="1">Option 1</Menu.Item>
          <Menu.Item key="2">Option 2</Menu.Item>
          <Menu.Item key="3">Option 3</Menu.Item>
          <Menu.Item key="4">Option 4</Menu.Item>
        </SubMenu>
        <SubMenu key="sub2" icon={<AppstoreOutlined />} title="Navigation Two">
          <Menu.Item key="5">Option 5</Menu.Item>
          <Menu.Item key="6">Option 6</Menu.Item>
          <SubMenu key="sub3" title="Submenu">
            <Menu.Item key="7">Option 7</Menu.Item>
            <Menu.Item key="8">Option 8</Menu.Item>
          </SubMenu>
        </SubMenu>
        <SubMenu key="sub4" icon={<SettingOutlined />} title="Navigation Three">
          <Menu.Item key="9">Option 9</Menu.Item>
          <Menu.Item key="10">Option 10</Menu.Item>
          <Menu.Item key="11">Option 11</Menu.Item>
          <Menu.Item key="12">Option 12</Menu.Item>
        </SubMenu>
      </Menu>
        <table className="table table-bordered  table-hover">
          <thead>
            <tr>
              <th style={{ width: '5%' }} className="text-center">id</th>
              <th style={{ width: '5%' }} className="text-center">autoID</th>
              <th style={{ width: '15%' }} className="text-center">Date</th>
              <th style={{ width: '15%' }} className="text-center">Start Time</th>
              <th style={{ width: '10%' }} className="text-center">Gender</th>
              <th style={{ width: '15%' }} className="text-center">Date Of Birth</th>
              <th style={{ width: '15%' }} className="text-center">Phone Number</th>
              <th style={{ width: '20%' }} className="text-center">Hành Động</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map((el, index) => {
              return (
                <tr key={index}>
                  <td className="text-center">{el.id}</td>
                  <td className="text-center">{el.id_name}</td>
                  <td className="text-center">{el.date}</td>
                  <td className="text-center">{el.start_time}</td>
                  <td className="text-center">{el.gender}</td>
                  <td className="text-center">{el.dateofbirth}</td>
                  <td className="text-center">{el.phonenumber}</td>
                  <td>
                    <Link className="btn btn-primary" style={{ margin: ' 0 40px 0 30px' }} to={`/edit/${el.id}`}>Edit</Link>
                    <button type="button" className="btn btn-danger" onClick={() => this.deleteItem(el.id)}>Xóa</button>
                  </td>
                </tr>
              )
            })}
          </tbody>
        </table>
      </div>
    </div>;
  }
}

export default Home;
